select substring(iban,1,2) as country, M.status, frequency_unit as frequency, count(*) as nb, sum(amount) sum, avg(amount) avg from civicrm_contribution_recur  as R join civicrm_sdd_mandate M on R.id=M.entity_id and M.entity_table="civicrm_contribution_recur"  where contribution_status_id=2 group by status, country, frequency_unit order by country;
